DROP TABLE PROVINCE CASCADE CONSTRAINTS;

CREATE TABLE PROVINCE 
(
  PROVINCE_CODE VARCHAR2(6) NOT NULL 
, PROVINCE_NAME VARCHAR2(30) NOT NULL 
, PROVINCE_NAME_SHORT VARCHAR2(10) NOT NULL 
, CREATE_DATE DATE 
, CREATE_USER VARCHAR2(20) 
, UPDATE_DATE DATE 
, UPDATE_USER VARCHAR2(20) 
);

COMMENT ON TABLE PROVINCE IS '省份信息';
COMMENT ON COLUMN PROVINCE.PROVINCE_CODE IS '省份代码';
COMMENT ON COLUMN PROVINCE.PROVINCE_NAME IS '省份名称';
COMMENT ON COLUMN PROVINCE.PROVINCE_NAME_SHORT IS '省份名称简称';
COMMENT ON COLUMN PROVINCE.CREATE_DATE IS '创建时间';
COMMENT ON COLUMN PROVINCE.CREATE_USER IS '创建者';
COMMENT ON COLUMN PROVINCE.UPDATE_DATE IS '更新时间';
COMMENT ON COLUMN PROVINCE.UPDATE_USER IS '更新者';

ALTER TABLE PROVINCE ADD CONSTRAINT PROVINCE_PK PRIMARY KEY (PROVINCE_CODE);
