DROP TABLE SCORE_PROVINCE CASCADE CONSTRAINTS;

CREATE TABLE SCORE_PROVINCE 
(
  PROVINCE_CODE VARCHAR2(6) NOT NULL 
, YEAR VARCHAR2(4) NOT NULL 
, STUDENT_CATEGORY_CODE VARCHAR2(2) NOT NULL 
, BATCH_CODE VARCHAR2(2) NOT NULL 
, SCORE NUMBER(3) NOT NULL 
, CREATE_DATE DATE 
, CREATE_USER VARCHAR2(20) 
, UPDATE_DATE DATE 
, UPDATE_USER VARCHAR2(20) 
);

COMMENT ON TABLE SCORE_PROVINCE IS '分数线(省份)';
COMMENT ON COLUMN SCORE_PROVINCE.PROVINCE_CODE IS '省份代码';
COMMENT ON COLUMN SCORE_PROVINCE.YEAR IS '年份';
COMMENT ON COLUMN SCORE_PROVINCE.STUDENT_CATEGORY_CODE IS '考生类别';
COMMENT ON COLUMN SCORE_PROVINCE.BATCH_CODE IS '批次';
COMMENT ON COLUMN SCORE_PROVINCE.SCORE IS '分数线';
COMMENT ON COLUMN SCORE_PROVINCE.CREATE_DATE IS '创建时间';
COMMENT ON COLUMN SCORE_PROVINCE.CREATE_USER IS '创建者';
COMMENT ON COLUMN SCORE_PROVINCE.UPDATE_DATE IS '更新时间';
COMMENT ON COLUMN SCORE_PROVINCE.UPDATE_USER IS '更新者';

ALTER TABLE SCORE_PROVINCE ADD CONSTRAINT SCORE_PROVINCE_PK PRIMARY KEY (PROVINCE_CODE, YEAR, STUDENT_CATEGORY_CODE, BATCH_CODE);