DROP TABLE SPECIALTY CASCADE CONSTRAINTS;

CREATE TABLE SPECIALTY 
(
  SPECIALTY_CODE VARCHAR2(6) NOT NULL 
, SPECIALTY_NAME VARCHAR2(100) NOT NULL 
, SPECIALTY_CATEGORY VARCHAR2(2) NOT NULL 
, SPECIALTY_PROPERTY VARCHAR2(2) NOT NULL 
, CREATE_DATE DATE 
, CREATE_USER VARCHAR2(20) 
, UPDATE_DATE DATE 
, UPDATE_USER VARCHAR2(20) 
);

COMMENT ON TABLE SPECIALTY IS '专业信息';
COMMENT ON COLUMN SPECIALTY.SPECIALTY_CODE IS '专业代码';
COMMENT ON COLUMN SPECIALTY.SPECIALTY_NAME IS '专业名称';
COMMENT ON COLUMN SPECIALTY.SPECIALTY_CATEGORY IS '专业类别';
COMMENT ON COLUMN SPECIALTY.SPECIALTY_PROPERTY IS '专业所属';
COMMENT ON COLUMN SPECIALTY.CREATE_DATE IS '创建时间';
COMMENT ON COLUMN SPECIALTY.CREATE_USER IS '创建者';
COMMENT ON COLUMN SPECIALTY.UPDATE_DATE IS '更新时间';
COMMENT ON COLUMN SPECIALTY.UPDATE_USER IS '更新者';

ALTER TABLE SPECIALTY ADD CONSTRAINT SPECIALTY_PK PRIMARY KEY (SPECIALTY_CODE);