DROP TABLE SPECIALTY_COLLEGE CASCADE CONSTRAINTS;

CREATE TABLE SPECIALTY_COLLEGE 
(
  COLLEGE_CODE VARCHAR2(9) NOT NULL 
, COLLEGE_NAME VARCHAR2(100) NOT NULL 
, SPECIALTY_CODE VARCHAR2(6) NOT NULL 
, SPECIALTY_NAME VARCHAR2(100) NOT NULL 
, SPECIALTY_CATEGORY VARCHAR2(2) NOT NULL 
, CREATE_DATE DATE 
, CREATE_USER VARCHAR2(20) 
, UPDATE_DATE DATE 
, UPDATE_USER VARCHAR2(20) 
);

COMMENT ON TABLE SPECIALTY_COLLEGE IS '院校专业';
COMMENT ON COLUMN SPECIALTY_COLLEGE.COLLEGE_CODE IS '院校代码';
COMMENT ON COLUMN SPECIALTY_COLLEGE.COLLEGE_NAME IS '院校名称';
COMMENT ON COLUMN SPECIALTY_COLLEGE.SPECIALTY_CODE IS '专业代码';
COMMENT ON COLUMN SPECIALTY_COLLEGE.SPECIALTY_NAME IS '专业名称';
COMMENT ON COLUMN SPECIALTY_COLLEGE.SPECIALTY_CATEGORY IS '专业类别';
COMMENT ON COLUMN SPECIALTY_COLLEGE.CREATE_DATE IS '创建时间';
COMMENT ON COLUMN SPECIALTY_COLLEGE.CREATE_USER IS '创建者';
COMMENT ON COLUMN SPECIALTY_COLLEGE.UPDATE_DATE IS '更新时间';
COMMENT ON COLUMN SPECIALTY_COLLEGE.UPDATE_USER IS '更新者';

ALTER TABLE SPECIALTY_COLLEGE ADD CONSTRAINT SPECIALTY_COLLEGE_PK PRIMARY KEY (COLLEGE_CODE, SPECIALTY_CODE);